import React, { Component } from "react";
import "./App.css";

class App extends Component {
  state = {
    first: "Hello There",
    second: "How are you",
    weekDays: [
      "Monday",
      "Tuesday",
      "wedenesday",
      "Thursday",
      "Friday",
      "saturady",
      "Sunday"
    ]
  };
  cssClass = () => {
    return { color: "red", fontSize: "4em" };
  };

  //    me =()=> {
  //   {this.state.weekDays.map(x => (
  //     <li>{x}</li>
  //   ))
  // }
  me = () => {
    this.state.weekDays.forEach(x => {
      return <li id={x}>{x}</li>;
    });
  };

  render() {
    return (
      <div>
        <h1 style={this.cssClass()}>{this.state.first}</h1>
        <h3>{this.state.second}</h3>
        <ul>{this.me()}</ul>
      </div>
    );
  }
}

export default App;
