import React, { Component } from "react";
import "./App.css";

class App extends Component {
  state = {
    first: "Hello There",
    second: "How are you",
    counter: 0,
    weekDays: [
      "Monday",
      "Tuesday",
      "wednesday",
      "Thursday",
      "Friday",
      "saturday",
      "Sunday"
    ]
  };
  cssClass = () => {
    return { color: "red", fontSize: "4em" };
  };

  //    me =()=> {
  //   {this.state.weekDays.map(x => (
  //     <li>{x}</li>
  //   ))
  // }

  counterUp = () => {
    this.setState({ counter: this.state.counter + 1 });
  };
  counterUp5 = () => {
    this.setState({ counter: this.state.counter + 5 });
  };
  render() {
    const arr = this.state.weekDays.map(x => <li key={x}>{x}</li>);
    var style = {
      color: "white",
      fontSize: 200
    };
    return (
      <div>
        <h1 style={this.cssClass()}>{this.state.first}</h1>
        <h3>{this.state.second}</h3>
        <ul>{arr}</ul>
        <button onClick={this.counterUp}>+1</button>
        <button onClick={this.counterUp5}>+5</button>
        <p style={style}>{this.state.counter}</p>
      </div>
    );
  }
}

export default App;
